import React from 'react';
import './App.css';
import Login from './components/Login'
import HomeContainer from './components/HomeContainer'
import {BrowserRouter, Route} from 'react-router-dom'

export default class App extends React.Component {
  state = {
    selectedPage: 'login',
    loggedInUser: null,
}

componentDidMount() {
    fetch('http://localhost:3000/authorize', {
        headers: {
            Authorization: `Bearer ${localStorage.token}`
        }
    })
    .then(res => res.json())
    .then(profile => {
        if (!profile.failed) {
            this.setState({
                loggedInUser: profile,
            })
        }
    })
}

selectPage = page => {
    this.setState({
        selectedPage: page
    })
}



  render(){
    return (
      <div className="App">
        <BrowserRouter>
          <div>
          <Route exact to={"http://localhost:3000/login"} component={Login} />
          <Route exact to={"http://localhost:3000/home/:id"} component={HomeContainer} />
          </div>
        </BrowserRouter>
      </div>
    );
  }
}
