import React from 'react'
import EmployeeCard from './EmployeeCard'

export default class HomeContainer extends React.Component{
    state = {
        user: {
            restaurants: []
        }
    }

    componentDidMount(){
        fetch("http://localhost:3000/get_user_by_token", {
            headers: {
                Authorization: `Bearer ${localStorage.token}`
            }
        }).then(res => res.json())
        .then(res => this.setState({user: res}))
        // .then(console.log(this.state.user.restaurants))
    }
    render(){
        return(
            <div>
                {this.state.user.restaurants.foreach(restaurant => {
                    restaurant.employees.each(employee => (<EmployeeCard employee={employee} restaurant={restaurant}/>))
                })}
            </div>
        )
    }
}
